typedef struct {
   Elf64_Word    type;
   Elf64_Word    key_size;
   Elf64_Word    value_size;
   Elf64_Word    max_entries;
   Elf64_Word    inner_map_idx;
   unsigned char platform_specific_data[];
} Elf64_BpfMapDefinition;

typedef struct {
   Elf64_Half    magic;
   unsigned char version;
   unsigned char flags;
   Elf64_Word    hdr_len;
   Elf64_Word    func_info_off;
   Elf64_Word    func_info_len;
   Elf64_Word    line_info_off;
   Elf64_Word    line_info_len;
   unsigned char platform_specific_data[];
} Elf64_BtfExtHeader;

typedef struct {
    Elf64_Word           func_info_rec_size;
    Elf64_BtfExtInfoSec  btf_ext_info_sec[];
} Elf64_BpfFunctionInfo;

typedef struct {
   Elf64_Word    sec_name_off;
   Elf64_Word    num_info;
   unsigned char data[];
} Elf64_BtfExtInfoSec;

typedef struct {
    Elf64_Word insn_off;
    Elf64_Word type_id;
} Elf64_BpfFunctionInfo;

typedef struct {
    Elf64_Word           line_info_rec_size;
    Elf64_BtfExtInfoSec  btf_ext_info_sec[];
} Elf64_BpfLineInfo;

typedef struct {
    Elf64_Word insn_off;
    Elf64_Word file_name_off;
    Elf64_Word line_off;
    Elf64_Word line_col;
} ELF32_BpfLineInfo;
